function crackPassword(password, crimeDefinition) {
  if (password.length < crimeDefinition.minPasswordLength) {
    return null;
  }

  let reducedSortedList = generateReducedSortedList(password);
  let guessedPasswords = reduceList(reducedSortedList, password);

  if (guessedPasswords.length > 0) {
    return { password: password, crimeDefinition: crimeDefinition, guessedPasswords };
  }

  return null;
}

function generateReducedSortedList(password) {
  let reducedSortedList = [];
  let sortedList = generateSortedList(password);
  reducedSortedList = reducedSortedList.concat(sortedList);
  return reducedSortedList;
}

function generateSortedList(password) {
  let sortedList = [];
  let characterCount = password.length;
  for (let i = 0; i < characterCount; i++) {
    sortedList.push(password[i]);
  }
  return sortedList;
}

function reduceList(list, currentPassword) {
  let guessedPasswords = [];
  for (let password of list) {
    if (isMatch(currentPassword, password)) {
      guessedPasswords.push(password);
    }
  }
  return guessedPasswords;
}

